cart = [];

function displayProducts(productsData, who = "productwrapper") {
  let productsString = "";

  productsData.forEach(function (product, index) {
    let { id, name, image, color, description, price, size } = product;

    if (who == "productwrapper") {
      productsString += ` <div class="product">
        <div class="prodimg">
          <img src="${image}" width="100%" />
        </div>
        <h3>${name}</h3>
        <p>Price : ${price}$</p>
        <p>Size : ${size}</p>
        <p>Color : ${color}</p>
        <p>${description}</p>
        <p>
          <button onclick="addToCart(${id})">Add to Cart</button>
        </p>
      </div>`;
    } else if (who == "cart") {
      productsString += ` <div class="product">
        <div class="prodimg">
          <img src="${image}" width="100%" />
        </div>
        <h3>${name}</h3>
        <p>Price : ${price}$</p>
        <p>Size : ${size}</p>
        <p>Color : ${color}</p>
        <p>${description}</p>
        <p>
          <button onclick="removeFromCart(${id})">Remove from Cart</button>
        </p>
      </div>`;
    }
  });

  document.getElementById(who).innerHTML = productsString;
}

displayProducts(products);

function searchProduct(searchValue) {
  let searchedProducts = products.filter(function (product, index) {
    let searchString =
      product.name + " " + product.color + " " + product.description;

    return searchString.toUpperCase().indexOf(searchValue.toUpperCase()) != -1;
  });

  displayProducts(searchedProducts);
}


function getProductByID(productArray, id) {
  return productArray.find(function (product) {
    return product.id == id;
  });
}

function addToCart(id) {
  let checkCart = cart.findIndex(function(product){
    if(product.id == id)
      return true;
  });

  if(checkCart != -1){
    alert("The Product is already present in the cart");
  }else{
    let pro = getProductByID(products, id);
    cart.push(pro);
    document.getElementById("cartItems").innerText = `Total items in cart : ${cart.length}`;
    displayProducts(cart, "cart"); 
  }

  
}

function removeFromCart(id) {
  let index = cart.findIndex(function (product) {
    return product.id == id;
  });
  cart.splice(index, 1);
  document.getElementById("cartItems").innerText = `Total items in cart : ${cart.length}`;
  displayProducts(cart, "cart");
}

function filterPrice(){
  let minPrice = document.getElementById("minPrice").value;
  let maxPrice = document.getElementById("maxPrice").value;

  let filteredProducts = products.filter(function (product, index) {
    return product.price >= minPrice && product.price <= maxPrice;
  });

  displayProducts(filteredProducts);

  
}